import { faQuoteLeft, faQuoteRight } from '@fortawesome/free-solid-svg-icons'
import { faTwitter } from '@fortawesome/free-brands-svg-icons'
import { useState } from 'react';

let name;
let quote;


function newQuote() {

}

function QuoteContent() {
  return (
    <>
      <div className='quote-text'>
      <div className='quote-icon left'>
        <FontAwesomeIcon icon={faQuoteLeft} />
      </div>
      <p id="text">Quote text here adfjakdjfadejladcdc.</p>
      <div className='quote-icon right'>
        <FontAwesomeIcon icon={faQuoteRight} />
      </div>
      </div>
      <div className='quote-author'>
        <p id="author">first last</p>
      </div>
    </>
  )
}

function ButtonContent() {
  return (
    <>
      <div className='button-div'>
      <div className='share'>
        <a id="tweet-quote" href="twitter.com/intent/tweet" target='_blank'><FontAwesomeIcon icon={faTwitter} /></a>
      </div>
      <div className='new-button'>
        <button id="new-quote" onClick={() => newQuote()}>
          New Quote
        </button>
      </div>
      </div>
    </>
  )
}

const QuotePage = () => {
  const [quoteText, setQuoteText] = useState('');
  const [quoteAuthor, setQuoteAuthor] = useState('');
  return (
    <>
      <QuoteContent />
      <ButtonContent />

    </>
  )
}

export default QuotePage
