import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faQuoteLeft, faQuoteRight } from '@fortawesome/free-solid-svg-icons'
import { faTwitter } from '@fortawesome/free-brands-svg-icons'
import { useState } from 'react';





function ButtonContent({qAuthor, qText, onButton}) {

  async function fetchQuote() {
    fetch("https://api.quotable.io/random")
      .then(response => {
        return response.json()
      })
      .then(data => {
        setQuoteText(data.content);
        setQuoteAuthor(data.author);
      })
  
    let qa = {name: '', quote: ''};
    const response = await fetch("https://api.quotable.io/random");
    const quoteJSON = await response.json();
    if(response.ok) {
      console.log(`if ok ${quoteJSON.author}`);
      qa.name = quoteJSON.author;
      qa.quote = quoteJSON.content;
    } else {
        qa.name = `error: ${response.statusText}`;
        qa.quote = response.status;
    }
    return qa;
  }

  function newQuote() {
    console.log("clicked");
    onButton(getQuote());
  }

  const regex = / /ig;
  let tweetLink = qText.replace(regex, '%20') + "%20--%20" + qAuthor.replace(regex, '%20');
  
  return (
    <>
      <div className='button-div'>
      <div className='share'>
        <a id="tweet-quote" href={`twitter.com/intent/tweet?text=${tweetLink}`} target='_blank'><FontAwesomeIcon icon={faTwitter} /></a>
      </div>
      <div className='new-button'>
        <button id="new-quote" onClick={() => newQuote()}>
          New Quote
        </button>
      </div>
      </div>
    </>
  )
}

export default function QuotePage() {
  const [quoteText, setQuoteText] = useState("test text");
  const [quoteAuthor, setQuoteAuthor] = useState("test author");

  function QuoteContent({qText, qAuthor}) {
    console.log("hi" + qText);
    return (
      <>
        <div className='quote-text'>
        <div className='quote-icon left'>
          <FontAwesomeIcon icon={faQuoteLeft} />
        </div>
        <p id="text">{qText}</p>
        <div className='quote-icon right'>
          <FontAwesomeIcon icon={faQuoteRight} />
        </div>
        </div>
        <div className='quote-author'>
          <p id="author">{qAuthor}</p>
        </div>
      </>
    )
  }

  function handleButton(tmp) {
    
    console.log("hi im handleButton");
    console.log("before " + quoteText);
    console.log("tmp = " + tmp);
    setQuoteText("tmp.name");
    setQuoteAuthor("tmp.author");
    console.log("after " + quoteText);
  }

  return (
    <>
      <QuoteContent qAuthor={quoteAuthor}  qText={quoteText}/>
      <ButtonContent qText={quoteText} qAuthor={quoteAuthor} onButton={handleButton} />
    </>
  )
}
